import React from "react";
import logo from "../img/logo.png";

const Hero = () => {
  return (
    <div className="relative bg-gradient-to-b from-teal-700 to-teal-900 text-white py-20">
      {/* Logo aligned to the left */}
      <div className="absolute left-0 top-1/2 transform -translate-y-1/2 ml-8">
        <img
          src={logo}
          alt="Logo"
          className="w-60 rounded-full bg-gray-300 p-4 shadow-lg"
        />
      </div>

      {/* Centered text */}
      <div className="flex justify-center items-center h-full">
        <h1 className="text-5xl font-bold leading-tight text-center">
          لنجعل الذكر هتافة عالمية
        </h1>
      </div>

      {/* Wave SVG at the bottom */}
      <div className="absolute bottom-0 left-0 w-full overflow-hidden leading-none">
        <svg
          className="relative block w-full h-24"
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 1440 320"
        >
          <path
            fill="#ffffff"
            fillOpacity="1"
            d="M0,256L48,245.3C96,235,192,213,288,197.3C384,181,480,171,576,181.3C672,192,768,224,864,229.3C960,235,1056,213,1152,208C1248,203,1344,213,1392,218.7L1440,224L1440,320L1392,320C1344,320,1248,320,1152,320C1056,320,960,320,864,320C768,320,672,320,576,320C480,320,384,320,288,320C192,320,96,320,48,320L0,320Z"
          ></path>
        </svg>
      </div>
    </div>
  );
};

export default Hero;
