import React from "react";
import { Link } from "react-router-dom";

const Login = () => {
  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <div className="w-full max-w-md bg-white p-10 rounded-lg shadow-lg">
        <h2 className="text-3xl font-bold mb-8 text-center text-gray-800">
          تسجيل الدخول
        </h2>

        <form>
          {/* عنوان البريد الإلكتروني */}
          <div className="mb-6">
            <label className="block text-gray-700 mb-2">
              عنوان البريد الإلكتروني
            </label>
            <input
              type="email"
              className="w-full px-4 py-3 text-right border rounded-md focus:outline-none focus:ring-2 focus:ring-teal-500"
              placeholder="أدخل عنوان البريد الإلكتروني"
            />
          </div>

          {/* كلمة المرور */}
          <div className="mb-6">
            <label className="block text-gray-700 mb-2">كلمة المرور</label>
            <input
              type="password"
              className="w-full px-4 py-3 text-right border rounded-md focus:outline-none focus:ring-2 focus:ring-teal-500"
              placeholder="أدخل كلمة المرور"
            />
          </div>

          {/* نسيت كلمة المرور */}
          <div className="flex justify-between items-center mb-6">
            <div>
              <Link to="/forgotpass" className="text-sm text-teal-600 hover:text-teal-700">
                نسيت كلمة المرور؟
              </Link>
            </div>
          </div>

          {/* زر تسجيل الدخول */}
          <button
            type="submit"
            className="w-full py-3 bg-teal-600 text-white rounded-md hover:bg-teal-700 focus:outline-none focus:ring-2 focus:ring-teal-500"
          >
            تسجيل الدخول
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
