import React from "react";
import logo  from"../img/logo.png"
const Hero = () => {
  return (
    <div className="bg-gradient-to-b from-teal-700 to-teal-900 text-white py-20 text-center">
      <img
        src={logo}
        alt="Logo"
        className="w-60 md:w-5/5 z-50 rounded-full bg-gray-300"
      />
      <div className="max-w-xl mx-auto">
        <h1 className="my-4 text-5xl font-bold leading-tight ">
          لنجعل الذكر هتافة عالمية
        </h1>
      </div>

    </div>
  );
};


export default Hero;
