import React from "react";
import logo  from"..logo.png"
const Hero = () => {
  return (
    <div className="bg-gradient-to-b from-teal-700 to-teal-900 text-white py-20 text-center">
      <div className="max-w-xl mx-auto">
        <img
                  src={logo}
          alt="Logo"
          className="w-40 mx-auto mb-8"
        />
        <h1 className="text-5xl font-bold mb-4"> لنجعل الذكر هتافة عالمية</h1>
      </div>
    </div>
  );
};


export default Hero;
