import React from "react";

const SignUp = () => {
  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <div className="w-full max-w-3xl bg-white p-10 rounded-lg shadow-lg">
        <h2 className="text-3xl font-bold mb-8 text-center text-gray-800">
          التسجيل في الموقع
        </h2>

        <form>
          {/* الاسم الأول والكنية */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mb-6">
            {/* الاسم الأول */}
            <div className="relative order-1 md:order-2">
              <label className="block text-gray-700 mb-2">الاسم الأول</label>
              <input
                type="text"
                className="w-full px-4 py-3 text-right border rounded-md focus:outline-none focus:ring-2 focus:ring-teal-500"
                placeholder="الاسم الأول"
              />
            </div>

            {/* الكنية */}
            <div className="relative order-2 md:order-1">
              <label className="block text-gray-700 mb-2">الكنية</label>
              <input
                type="text"
                className="w-full px-4 py-3 text-right border rounded-md focus:outline-none focus:ring-2 focus:ring-teal-500"
                placeholder="الكنية"
              />
            </div>
          </div>

          {/* البريد الإلكتروني والجنسية */}
