import React from "react";

const Hero = () => {
  return (
    // Hero section container with background color and padding
    <div className="bg-teal-700 text-white py-16 px-8 text-center">
      {/* Content container, centered on the page */}
      <div className="max-w-xl mx-auto">
        {/* Logo image */}
        <img
          src="path_to_your_logo.png"
          alt="Logo"
          className="w-40 mx-auto mb-8"
        />

        {/* Main title */}
        <h1 className="text-4xl font-bold">دعاء الوالد وما ولد</h1>

        {/* Religious quote */}
        <p className="mt-4 text-lg">
          {`رَبَّنَا وَاجْعَلْنَا مُسْلِمَيْنِ لَكَ وَمِن ذُرِّيَّتِنَا أُمَّةً مُّسْلِمَةً لَّكَ وَأَرِنَا مَنَاسِكَنَا وَتُبْ عَلَيْنَا إِنَّكَ أَنتَ التَّوَّابُ الرَّحِيمُ`}
        </p>

        {/* Counter showing number of mentions */}
        <div className="mt-4 text-2xl">63,910 عدد الذكر</div>
      </div>
    </div>
  );
};

export default Hero;
