import React from "react";

const Hero = () => {
  return (
    <div className="bg-gradient-to-b from-teal-700 to-teal-900 text-white py-20 text-center">
      <div className="max-w-xl mx-auto">
        <img
          src="path_to_your_logo.png"
          alt="Logo"
          className="w-40 mx-auto mb-8"
        />
        <h1 className="text-5xl font-bold mb-4">دعاء الوالد وما ولد</h1>
        <p className="mb-6 text-xl">
          {`رَبَّنَا وَاجْعَلْنَا مُسْلِمَيْنِ لَكَ وَمِن ذُرِّيَّتِنَا أُمَّةً مُّسْلِمَةً لَّكَ وَأَرِنَا مَنَاسِكَنَا وَتُبْ عَلَيْنَا إِنَّكَ أَنتَ التَّوَّابُ الرَّحِيمُ`}
        </p>
        <div className="text-3xl font-semibold">63,910 عدد الذكر</div>
      </div>
    </div>
  );
};


export default Hero;
