import React from "react";

const Video = () => {
  const videos = [
    {
      id: "https://youtu.be/f1kuQch_xcE?si=GKbofoWxHj385V4u",
      title: "أعظم حجة في الإسلام .. وأثرها في أمة الحبيب ﷺ",
    },
    {
      id: "VIDEO_ID_2",
      title: "درس 2 - الشيخ عون القدومي",
    },
    {
      id: "VIDEO_ID_3",
      title: "درس 3 - الشيخ عون القدومي",
    },
  ];

  return (
    <div className="py-16 px-8 text-center bg-white">
      <h2 className="text-3xl font-bold text-teal-700 mb-8">
        من دروس مولانا الشيخ عون
      </h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8 justify-center">
        {videos.map((video, index) => (
          <div key={index} className="max-w-m mx-auto bg-white p-4 rounded-lg shadow-lg">
            <div className="relative pb-56">
              <iframe
                width="100%"
                height="200"
                src={`https://www.youtube.com/embed/${video.id}`}
                title={`YouTube video - ${video.title}`}
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
                className="rounded-lg"
              ></iframe>
            </div>
            <p className="mt-4 text-teal-700">{video.title}</p>
            <a
              href={`https://www.youtube.com/watch?v=${video.id}`}
              target="_blank"
              rel="noopener noreferrer"
              className="mt-4 inline-block bg-teal-600 text-white px-4 py-2 rounded-full hover:bg-teal-500 transition-colors duration-300"
            >
              تشغيل الفيديو
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Video;
