import React from "react";
import logo from "../img/logo.png";
import "./App.css";
const Hero = () => {
  return (
    <div className="relative bg-gradient-to-b from-teal-700 to-teal-900 text-white pt-20 pb-40">
      {/* Logo aligned to the left */}
      <div className="absolute left-8 top-1/2 transform -translate-y-1/2">
        <img
          src={logo}
          alt="Logo"
          className="w-72 h-72 rounded-full bg-gray-300 p-4 shadow-lg"
        />
      </div>

      {/* Centered text */}
      <div className="max-w-3xl mx-auto flex flex-col items-center justify-center h-full text-center">
        <h1 className="text-5xl font-bold leading-tight">
          لنجعل الذكر هتافة عالمية
        </h1>
      </div>

      {/* Custom wave shape at the bottom */}
      <div className="absolute bottom-0 left-0 w-full">
        <div className="custom-wave-bg"></div>
      </div>
    </div>
  );
};

export default Hero;
