import React from "react";
import logo from "../img/logo.png";

const Hero = () => {
  return (
    <div className="relative bg-gradient-to-b from-teal-700 to-teal-900 text-white py-20">
      {/* Logo aligned to the left */}
      <div className="absolute left-8 top-1/2 transform -translate-y-1/2">
        <img
          src={logo}
          alt="Logo"
          className="w-72 h-72 rounded-full bg-gray-300 p-4 shadow-lg"
        />
      </div>

      {/* Centered text */}
      <div className="max-w-3xl mx-auto flex flex-col items-center justify-center h-full text-center">
        <h1 className="text-5xl font-bold leading-tight">
          دعاء الوالد وما ولد
        </h1>
        <p className="mt-4 text-2xl">
          {`رَبَّنَا وَاجْعَلْنَا مُسْلِمَيْنِ لَكَ وَمِن ذُرِّيَّتِنَا أُمَّةً مُّسْلِمَةً لَّكَ وَأَرِنَا مَنَاسِكَنَا وَتُبْ عَلَيْنَا إِنَّكَ أَنتَ التَّوَّابُ الرَّحِيمُ`}
        </p>
        <p className="mt-2 text-lg">تم إتمام الحملة بفضل الله</p>
      </div>

      {/* Improved Wave SVG at the bottom */}
      <div className="absolute bottom-0 left-0 w-full overflow-hidden leading-none">
        <svg
          className="relative block w-full h-40"
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 1440 320"
        >
          <path
            fill="#ffffff"
            fillOpacity="1"
            d="M0,256L60,240C120,224,240,192,360,176C480,160,600,160,720,181.3C840,203,960,245,1080,240C1200,235,1320,181,1380,154.7L1440,128L1440,320L1380,320C1320,320,1200,320,1080,320C960,320,840,320,720,320C600,320,480,320,360,320C240,320,120,320,60,320L0,320Z"
          ></path>
        </svg>
      </div>
    </div>
  );
};

export default Hero;
