// Importing React library
import React from "react";

const CardsSection = () => {
  return (
    // Section container with padding and background color
    <div className="bg-gray-30000 py-16 px-8">
      {/* Container to hold the cards, using flexbox for layout */}
      <div className="max-w-6xl mx-auto grid md:grid-cols-3 gap-8">
        {/* Card 1 */}
        <div className="bg-white text-teal-800 p-8 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-300">
          <h3 className="text-2xl font-semibold mb-4">عنوان الكارد الأول</h3>
          <p className="text-lg mb-6">
            هذا وصف للكارد الأول. يمكن أن يحتوي هذا الوصف على تفاصيل مختصرة عن
            المحتوى أو الخدمة المقدمة.
          </p>
          <button className="bg-teal-600 text-white px-4 py-2 rounded-full hover:bg-teal-500 transition-colors duration-300">
            اقرأ المزيد
          </button>
        </div>

        {/* Card 2 */}
        <div className="bg-white text-teal-800 p-8 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-300">
          <h3 className="text-2xl font-semibold mb-4">عنوان الكارد الثاني</h3>
          <p className="text-lg mb-6">
            هذا وصف للكارد الثاني. يمكن أن يحتوي هذا الوصف على تفاصيل مختصرة عن
            المحتوى أو الخدمة المقدمة.
          </p>
          <button className="bg-teal-600 text-white px-4 py-2 rounded-full hover:bg-teal-500 transition-colors duration-300">
            اقرأ المزيد
          </button>
        </div>

        {/* Card 3 */}
        <div className="bg-white text-teal-800 p-8 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-300">
          <h3 className="text-2xl font-semibold mb-4">عنوان الكارد الثالث</h3>
          <p className="text-lg mb-6">
            هذا وصف للكارد الثالث. يمكن أن يحتوي هذا الوصف على تفاصيل مختصرة عن
            المحتوى أو الخدمة المقدمة.
          </p>
          <button className="bg-teal-600 text-white px-4 py-2 rounded-full hover:bg-teal-500 transition-colors duration-300">
            اقرأ المزيد
          </button>
        </div>
      </div>
    </div>
  );
};

export default CardsSection;
